mapper.select:

select 'false' as QUERYID,

from zhmv.order
where id = #{id,jdbcType=INTEGER}

mapper.update:
update zhmv.order

  
    status = #{param1.status,jdbcType=SMALLINT},
  
  
    pay_status = #{param1.payStatus,jdbcType=SMALLINT},
  
  
    pay_time = #{param1.payTime,jdbcType=SMALLINT},
  
  
    update_time = #{param1.updateTime,jdbcType=TIMESTAMP},
  

where id = #{param1.id,jdbcType=INTEGER} 
and status = #{param2,jdbcType=SMALLINT} 
and pay_status = #{param3,jdbcType=SMALLINT}

mapper.select:
  select count(id)
  from zhmv.order 
  
  	
       	start_time  >=  #{beginTime,jdbcType=TIMESTAMP}
  	 
  	
      	and start_time  <=  #{endTime,jdbcType=TIMESTAMP}

    select 
    from zhmv.order 
     
    	
         	start_time  >=  #{beginTime,jdbcType=TIMESTAMP}
    	 
    	
        	and start_time  <=  #{endTime,jdbcType=TIMESTAMP} 
    	
  	
    order by start_time desc 
	
	    limit 
	     #{offset,jdbcType=INTEGER},  
	    #{pagesize,jdbcType=INTEGER}
